        RF_fsrecID_ = (uint **)   new_vvector(1, RF_hdim, NRUTIL_UPTR);
        RF_mwcpPT_  = (uint **)   new_vvector(1, RF_hdim, NRUTIL_UPTR);
        RF_mwcpCT_  = (uint **)   new_vvector(1, RF_hdim, NRUTIL_UPTR);
